              </form>
            </div>
          </div>
        </div>
      {/* <SearchBox 
        onSubmit={handleSearch}
        onChange={handleInputChange}
      /> */}
      {/* {items?.length > 1 ? <BoxResults items={items.items} /> : ''} */}
