import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';

function ProductDetail() {
    const { id } = useParams();
    const [item, setItem] = useState('');

    useEffect(() => {
        return fetch('http://localhost:3001/api/items/' + id)
            .then((res) => res.json())
            .then(
                (data) => {
                    setItem(data);
                }
            )
            .catch((error) => console.log(error));
    }, []);

    return (
        <div className="productDetail">
            <div className="wrapper__container">
                <p className="boxResults__breadcrumb">
                    {"Electronica, Audio y video > Ipod > Reproductores > Ipod Touch > "}
                    <strong>32 GB</strong>
                </p>
                <div className="productDetail__container">
                    <img classname="productDetail__image" src={item?.picture} alt=""/>
                </div>

            </div>
            <h1>ProductDetail Page</h1>
        </div>
    )
}

export default ProductDetail
