import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import './ProductDetail.scss';

function ProductDetail() {
    const { id } = useParams();
    const [data, setData] = useState('');

    useEffect(() => {
        return fetch('http://localhost:3001/api/items/' + id)
            .then((res) => res.json())
            .then(
                (data) => {
                    setData(data);
                }
            )
            .catch((error) => console.log(error));
    }, []);

    return (
        <div className="productDetail">
            <div className="wrapper__container">
                <p className="boxResults__breadcrumb">
                    {"Electronica, Audio y video > Ipod > Reproductores > Ipod Touch > "}
                    <strong>32 GB</strong>
                </p>

                <div className="productDetail__container">
                    <div className="productDetail__image">
                        <img className="productDetail__image" src={data?.item?.picture} alt=""/>
                    </div>
                    <div className="productDetail__info">
                        <p className="productDetail__condition">
                            {data?.item?.condition ? 'Nuevo' : ''} - {data?.item?.sold_quantity + " Productos Vendidos"}

                        </p>
                    </div>
                </div>

            </div>
        </div>
    )
}

export default ProductDetail
