// import "./components/SearchBox/SearchBox.scss";
import logo from "./assets/images/Logo_ML.png";
import searchIcon from "./assets/images/ic_Search.png";
import {
  BrowserRouter as Router,
  Switch,
  Route
} from 'react-router-dom';

function App() {
  // const history = useHistory();
  const [searchTerm, setSearchTerm] = useState("");
  const [items, setItems] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  }
  const handleSearch = async (event) => {
    event.preventDefault();

    console.log(searchTerm);
    // history.push('/items?search=' + searchTerm);

    const response = await fetch("http://localhost:3001/api/items?q=" + searchTerm)
    const res = await response.json();
    console.log(res.items);
      // .then(res => res.json())
      // .then((data) => {
        // setIsLoaded(true);
        // console.log(data);

        setItems(res.items);
      // });

      console.log(items);
  };

  return (
    <div className="app">
      <div className="header">
        <div className="wrapper__container">
          <div className="header__navbar">
            <img className="header__logo" src={logo} alt="Mercado Libre Logo" />

            <form className="header__search" 
              method="GET"
              role="search"
              onSubmit={onSubmit}
            >
              <input
                className="header__searchInput"
                type="text"
                placeholder="Nunca dejes de buscar"
                onChange={onChange}
              />
              <button className="header__searchButton">
                <img src={searchIcon} alt="Search Button" />
              </button>
            </form>
          </div>
        </div>
      </div>
      {/* <SearchBox 
        onSubmit={handleSearch}
        onChange={handleInputChange}
      /> */}
      {/* {items?.length > 1 ? <BoxResults items={items.items} /> : ''} */}
      <BoxResults items={items} />
    </div>
  );
}

export default App;
