import React, { useState, useEffect } from 'react';
import "./App.scss";
import BoxResults from "./components/BoxResults/BoxResults";
import SearchBox from "./components/SearchBox/SearchBox";
// import "./components/SearchBox/SearchBox.scss";
import logo from "./assets/images/Logo_ML.png";
import searchIcon from "./assets/images/ic_Search.png";
import { BrowserRouter as Router, Link, useHistory } from 'react-router-dom';

function App() {
  const history = useHistory();
  const [searchTerm, setSearchTerm] = useState("");
  const [items, setItems] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  }
  const handleSearch = (event) => {
    event.preventDefault();
    history.push('/items?search=' + searchTerm);

    fetch("http://localhost:3001/api/items?q=" + searchTerm)
      .then(res => res.json())
      .then((data) => {
        // setIsLoaded(true);
        setItems(data.items);
      });

      console.log(items);
  };

  return (
    <Router>
      <div className="app">
        <div className="header">
          <div className="wrapper__container">
            <div className="header__navbar">
              <img className="header__logo" src={logo} alt="Mercado Libre Logo" />

              <form className="header__search" 
                method="GET"
                role="search"
                onSubmit={handleSearch}
              >
                <input
                  className="header__searchInput"
                  type="text"
                  placeholder="Nunca dejes de buscar"
                  value={searchTerm}
                  onChange={handleInputChange}
                />
                <button className="header__searchButton">
                  <img src={searchIcon} alt="Search Button" />
                </button>
              </form>
            </div>
          </div>
        </div>
        {/* <SearchBox /> */}
        {items.length > 1 ? <BoxResults items={items} /> : ''}
        {/* <BoxResults /> */}
      </div>
    </Router>
  );
}

export default App;
