import React, { useState, useEffect } from 'react';
import "./App.scss";
import BoxResults from "./components/BoxResults/BoxResults";
import SearchBox from "./components/SearchBox/SearchBox";
// import "./components/SearchBox/SearchBox.scss";
import logo from "./assets/images/Logo_ML.png";
import searchIcon from "./assets/images/ic_Search.png";
import { BrowserRouter as Router,
  Link,
  useHistory
} from 'react-router-dom';

function App() {
  const history = useHistory();
  const [searchTerm, setSearchTerm] = useState("");
  const [items, setItems] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  }
  const handleSearch = async (event) => {
    event.preventDefault();

    console.log(searchTerm);
    // history.push('/items?search=' + searchTerm);

    const response = await fetch("http://localhost:3001/api/items?q=" + searchTerm)
    const res = await response.json();
    console.log(res);
      // .then(res => res.json())
      // .then((data) => {
        // setIsLoaded(true);
        // console.log(data);

        setItems(res);
      // });

      console.log(items);
  };

  return (
      <div className="app">
        <SearchBox 
          onSubmit={handleSearch}
          onChange={handleInputChange}
        />
        {/* {items?.length > 1 ? <BoxResults items={items.items} /> : ''} */}
        <BoxResults items={items.items} />
      </div>
  );
}

export default App;
