import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import "./BoxResults.scss";
import Product from "./Product/Product";

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

function BoxResults(props) {
  const query = useQuery();
  const [searchResult, setSearchResult] = useState([]);
  const info = [];

  useEffect(() => {
    let queryString = query.get('search');
    if (queryString) {
      fetch("http://localhost:3001/api/items?q=" + queryString)
        .then(res => res.json())
        .then((response) => {
          setSearchResult(response.items);
        })
        .catch((error) => alert(error.message));
    }
  }, []);

  return (
    <div className="boxResults">
      <div className="wrapper__container">
        <p className="boxResults__breadcrumb">
          {"Electronica, Audio y video > Ipod > Reproductores > Ipod Touch > "}
          <strong>32 GB</strong>
        </p>

        <div className="boxResults__main">
          {/* {
            Array(searchResult)
            .fill()
            .map((item, i) => {
              return <Product 
                key={item.id} 
                item={item} />
            })
          } */}
          {searchResult?.map((item) => {
            return <Product 
              key={item.id}
              item={item}
            />
          })}
        </div>
      </div>
    </div>
  );
}

export default BoxResults;
