import React, { useState, Suspense, lazy } from 'react';
import "./App.scss";
// import "./components/SearchBox/SearchBox.scss";
import logo from "./assets/images/Logo_ML.png";
import searchIcon from "./assets/images/ic_Search.png";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from 'react-router-dom';
const ProductDetail = lazy(() => import('./components/BoxResults/Product/ProductDetail'));
const BoxResults = lazy(() => import("./components/BoxResults/BoxResults"));
const SearchBox = lazy(() => import("./components/SearchBox/SearchBox"));

function App() {
  const [searchTerm, setSearchTerm] = useState("");
  const [items, setItems] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  }

  const handleSearch = async (event) => {
    // event.preventDefault();
    return <Redirect
      to={{
        pathname: "/items",
        search: "?search=" + searchTerm,
        // state: { referrer: currentLocation }
      }}
    />
    // history.push('/items?search=' + searchTerm);
    // window.location('/items?search=' + searchTerm);

    // const response = await fetch("http://localhost:3001/api/items?q=" + searchTerm)
    // const res = await response.json();
    // console.log(res.items);
      // .then(res => res.json())
      // .then((data) => {
        // setIsLoaded(true);
        // console.log(data);

        // setItems(res.items);
      // });

      // console.log(items);
  };

  return (
    <div className="app">
      <Router>
        <Suspense fallback={<div>Loading...</div>}>
          <div className="header">
            <div className="wrapper__container">
              <div className="header__navbar">
                <img className="header__logo" src={logo} alt="Mercado Libre Logo" />

                <form className="header__search" 
                  method="GET"
                  role="search"
                  // action={'/items?search=' + searchTerm}
                  onSubmit={handleSearch}
                >
                  <input
                    className="header__searchInput"
                    type="text"
                    placeholder="Nunca dejes de buscar"
                    onChange={handleInputChange}
                  />
                  <button className="header__searchButton">
                    <img src={searchIcon} alt="Search Button" />
                  </button>
                </form>
              </div>
            </div>
          </div>
        {/* <SearchBox 
          onSubmit={handleSearch}
          onChange={handleInputChange}
        /> */}
          <Switch>
            <Route exact path="/items/:id">
              <ProductDetail />
            </Route>
            <Route exact path="/items">
              <BoxResults />
            </Route>
          </Switch>

        </Suspense>
      </Router>
      {/* <BoxResults items={items} /> */}
    </div>
  );
}

export default App;
